#!/usr/bin/env python

#calculate the AT content of a DNA string

seq = ""
numA = seq.count('A')
numT = seq.count('T')
seqlen = len(seq)
ATcontent = (numA + numT)/seqlen


print("number of A:", numA)
print("number of T:", numT)
print("total number of nt:", seqlen)
print("AT content of sequence:", ATcontent)
