#!/usr/bin/env python

# print out the reverse complement of a DNA string

seq = "5' 3'"
clnL = seq.lstrip("5'")
clnLR = clnL.rstrip(" 3'")

subAt = clnLR.replace('A', 't') 
subTa = subAt.replace('T', 'a') 
subCg = subTa.replace('C', 'g') 
subGc = subCg.replace('G', 'c') 
cap_allsubd = subGc.upper()
rev_comp = cap_allsubd[::-1]

print("\nOrig Seq:")
print(seq,"\n")
print("Comp:")
print("3'" + cap_allsubd + " 5'\n")
print("RevComp:")
print("5'" + rev_comp + " 3'\n\n\n")

